import React, { useState } from "react";
// import { useHistory } from "react-router-dom";


function AddItemsForm({addFoods}) {
    const [form, updateForm] = useState({name: "", 
                                        description:"",
                                        recipe: "", 
                                        serve: ""}
                                        );
    // const history = useHistory();

    function handleChange(e) {
      e.persist();
      updateForm(f => ({ ...f, [e.target.name]: e.target.value }));
    }

    function handleSubmit(e) {
      e.preventDefault();
      addItems({ form });
    //   history.push("/colors");
    }

    const {name, description, recipe, serve} = form;

    return (
      <div className="Form">
        <form onSubmit={handleSubmit}>
        <div>
            <label htmlFor="foodType"> Food Type</label>
            <select>
                <option value="snack">Snack</option>
                <option value="drink">Drink</option>
            </select>
          </div>
          <div>
            <label htmlFor="name"> Name</label>
            <input
              name="name"
              id="name"
              placeholder={"Enter" + {food_name} + "name"}
              onChange={handleChange}
              value={name}
            />
          </div>
          <div>
            <label htmlFor="description">Description</label>
            <input
              name="description"
              id="description"
              onChange={handleChange}
              value={description}
            />
          </div>

          <div>
            <label htmlFor="recipe">Recipe</label>
            <input
              name="recipe"
              id="recipe"
              onChange={handleChange}
              value={recipe}
            />
          </div>
          <div>
          <label htmlFor="serve">Serve</label>
            <input
              name="serve"
              id="serve"
              onChange={handleChange}
              value={serve}
            />
          </div>

          <input type="Submit" value={"Add this" + food_name} readOnly />
        </form>
      </div>
    );
  }

export default AddItemsForm;
